GtkWidget*
create_window2 (void)
{
  GtkWidget *window2;
  GtkWidget *fixed3;
  GtkWidget *gestionouvrier;

  window2 = gtk_window_new (GTK_WINDOW_TOPLEVEL);
  gtk_widget_set_size_request (window2, 1000, 800);
  gtk_window_set_title (GTK_WINDOW (window2), _("acceuil"));
  gtk_window_set_position (GTK_WINDOW (window2), GTK_WIN_POS_CENTER);

  fixed3 = gtk_fixed_new ();
  gtk_widget_show (fixed3);
  gtk_container_add (GTK_CONTAINER (window2), fixed3);

  gestionouvrier = gtk_button_new_with_mnemonic (_("gestion ouvrier"));
  gtk_widget_show (gestionouvrier);
  gtk_fixed_put (GTK_FIXED (fixed3), gestionouvrier, 376, 352);
  gtk_widget_set_size_request (gestionouvrier, 272, 96);

  g_signal_connect ((gpointer) gestionouvrier, "clicked",
                    G_CALLBACK (on_gestionouvrier_clicked),
                    NULL);

  /* Store pointers to all widgets, for use by lookup_widget(). */
  GLADE_HOOKUP_OBJECT_NO_REF (window2, window2, "window2");
  GLADE_HOOKUP_OBJECT (window2, fixed3, "fixed3");
  GLADE_HOOKUP_OBJECT (window2, gestionouvrier, "gestionouvrier");

  return window2;
}

